import React, { useState, useRef, useEffect } from "react";
import "./Carousel.css";
import Imgitem from "./Imgitem";
import img1 from "../assets/1.jpeg";
import img2 from "../assets/2.jpeg";
import img3 from "../assets/3.jpeg";
import img4 from "../assets/4.jpeg";
import img5 from "../assets/5.jpeg";

const totalItems = 4; // 전체 슬라이드 개수(총3개. 배열로 계산)

function Carousel() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const slideRef = useRef(null);

  // Next 버튼 클릭 시
  const NextSlide = () => {
    if (currentSlide >= totalItems) {
      // 더 이상 넘어갈 슬라이드가 없으면
      setCurrentSlide(0); // 1번째 사진으로 넘어갑니다.
      // return;  // 클릭이 작동하지 않습니다.
    } else {
      setCurrentSlide(currentSlide + 1);
    }
  };
  // Prev 버튼 클릭 시
  const PrevSlide = () => {
    if (currentSlide === 0) {
      setCurrentSlide(totalItems); // 마지막 사진으로 넘어갑니다.
      // return;  // 클릭이 작동하지 않습니다.
    } else {
      setCurrentSlide(currentSlide - 1);
    }
  };

  //   useEffect(() => {
  //     slideRef.current.style.transition = "all 0.5s ease-in-out";
  //     slideRef.current.style.transform = `translateX(-${currentSlide}00%)`; // 백틱을 사용하여 슬라이드로 이동하는 에니메이션을 만듭니다.
  //   }, [currentSlide]);

  return (
    <div className="carousel-wrapper">
      <div className="carousel">
        <Imgitem img={img1} />
        <Imgitem img={img2} />
        <Imgitem img={img3} />
        <Imgitem img={img4} />
        <Imgitem img={img5} />

        <div className="carousel_button--next" onClick={PrevSlide}></div>
        <div className="carousel_button--prev" onClick={NextSlide}></div>
      </div>
    </div>
  );
}

export default Carousel;
