import React, { useState } from "react";
import "./App.css";
import dummy from "./resource/dummyData";

import ContentList from "./pages/ContentList";
import Create from "./pages/Create";
import View from "./pages/View";

const App = (props) => {
  const [contents, setContents] = useState(dummy); // 담긴 데이터 불러오기
  const [clickCreate, setClickCreate] = useState(false); // 글쓰기 버튼이 클릭 되었는가?
  const [clickTitle, setClickTitle] = useState(true); // title이 눌렸는가?

  const [filteredContent, setFilteredTitle] = useState(dummy[dummy.length - 1]);

  const onCreateBtn = () => {
    setClickCreate((prev) => !prev);
  };

  const onClickTitle = () => {
    console.log("클릭했당 ");
    setClickTitle(true);
  };

  const handleSelect = (event) => {
    const selected = contents.filter((el) => {
      return el.title === event.target.value;
    });
    setFilteredTitle(selected);
  };

  return (
    <div className="App">
      <h1>React-CRUD-Project</h1>
      <div className="component">
        <div className="list">
          <header>
            <div></div>
            <h2>목록</h2>
            <button className="contentList__btn" onClick={onCreateBtn}>
              글쓰기
            </button>
          </header>
          <ContentList
            contents={contents}
            onClickTitle={onClickTitle}
          ></ContentList>
        </div>

        {/* {clickCreate ? (
          <Create contents={contents} setContents={setContents} />
        ) : (
          <div></div>
        )} */}

        {onClickTitle
          ? handleSelect.map((content) => {
              return <View contents={contents} key={content.id} />;
            })
          : contents.map((content) => {
              return <View contents={contents} key={content.id} />;
            })}
      </div>
    </div>
  );
};

export default App;
