import React from "react";
import "./View.css";

function View({ content, handleDelete, idx, handleUpdate }) {
  const selectedContent = contents.filter((content) => content.id === idx);
  return (
    <div className="View">
      <div className="View__item">
        <li className="View__list" key={selectedContent.id}>
          <div className="username">{selectedContent.username}</div>
          <div className="title">{selectedContent.title}</div>
          <div className="title">{selectedContent.content}</div>
          <div className="createAt">{selectedContent.createdAt}</div>
        </li>
      </div>
      <div className="View__btns">
        <button
          className="수정"
          onClick={() => handleUpdate(selectedContent.username, idx)}
        >
          수정
        </button>
        <button
          className="삭제"
          onClick={() => handleDelete(selectedContent.username, idx)}
        >
          삭제
        </button>
      </div>
    </div>
  );
}

export default View;
