import React, { useState } from "react";
import "./App.css";
import dummy from "./resource/dummyData";

import ContentList from "./pages/ContentList";
import Create from "./pages/Create";

const App = (props) => {
  const [contents, setContents] = useState(dummy);
  const [click, setclick] = useState(false);

  const onCreateBtn = () => {
    setclick((prev) => !prev);
  };

  return (
    <div className="App">
      <h1>React-CRUD-Project</h1>
      <div className="component">
        <div className="list">
          <ContentList contents={contents}></ContentList>
        </div>

        {click && <Create />}
      </div>
    </div>
  );
};

export default App;
