import React, { useState } from "react";
import "./App.css";
import dummy from "./resource/dummyData";

import ContentList from "./pages/ContentList";
import Create from "./pages/Create";
import View from "./pages/View";

const App = (props) => {
  const [contents, setContents] = useState(dummy); // 담긴 데이터 불러오기
  const [clickCreate, setClickCreate] = useState(false); // 글쓰기 버튼이 클릭 되었는가?

  const [isFiltered, setIsFiltered] = useState(false);
  const [filteredContent, setFilteredContent] = useState(dummy); //선택된 내용인가?

  // 글쓰기 버튼 클릭 여부에 따라 create 창 띄우기
  const onCreateBtn = () => {
    setClickCreate((prev) => !prev);
  };

  //filter 함수
  const handleFilter = (event) => {
    const filtered = contents.filter((content) => {
      return content.title === event.target.value;
    });
    console.log(`handleFilter 실행! ${event.target.value}`);
    setIsFiltered(true);
    setFilteredContent(filtered);
  };

  return (
    <div className="App">
      <h1>React-CRUD-Project</h1>
      <div className="component">
        <div className="list">
          <header>
            <div></div>
            <h2>목록</h2>
            <button className="contentList__btn" onClick={onCreateBtn}>
              글쓰기
            </button>
          </header>
          {contents.map((content) => {
            return (
              <ContentList
                content={content}
                handleFilter={handleFilter}
                key={content.id}
              />
            );
          })}
        </div>

        {clickCreate ? (
          <Create contents={contents} setContents={setContents} />
        ) : (
          <div></div>
        )}

        {isFiltered ? (
          contents.map((content) => {
            return <View content={content} key={content.id} />;
          })
        ) : (
          <div></div>
        )}
      </div>
    </div>
  );
};

export default App;
