import React, { useState } from "react";
import "./App.css";
import dummy from "./resource/dummyData";

import ContentList from "./pages/ContentList";
import Create from "./pages/Create";
import MainContent from "./pages/MainContent";

const App = (props) => {
  const nextId = dummy.length + 1;
  const [contents, setContents] = useState(dummy);
  const [clickCreate, setclickCreate] = useState(false);
  const [clickTitle, setclickTitle] = useState(false);

  const [username, setName] = useState("");
  const [title, setTitle] = useState("");
  const [msg, setMsg] = useState("");

  const onCreateBtn = () => {
    setclickCreate((prev) => !prev);
  };

  const onTitle = () => {
    setclickTitle((prev) => !prev);
  };

  const handleButtonClick = (event) => {
    const newcontent = {
      id: nextId,
      username: username,
      title: title,
      content: msg,
      createdAt: new Date(),
    };

    const newContents = [newcontent, ...contents];
    setContents(newContents);
    console.log(newContents);
  };

  const handleChangeTitle = (event) => {
    console.log(title);
    setTitle(event.target.value);
  };

  const handleChangeMsg = (event) => {
    console.log(msg);
    setMsg(event.target.value);
  };

  return (
    <div className="App">
      <h1>React-CRUD-Project</h1>
      <div className="component">
        <div className="list">
          <header>
            <div></div>
            <h2>목록</h2>
            <button className="contentList__btn" onClick={onCreateBtn}>
              글쓰기
            </button>
          </header>
          <ContentList
            contents={contents}
            setContents={setContents}
          ></ContentList>
        </div>

        {clickCreate ? (
          <Create
            contents={contents}
            setContents={setContents}
            username={username}
            title={title}
            msg={msg}
            handleButtonClick={handleButtonClick}
          />
        ) : (
          <div className="empty"></div>
        )}
        {clickTitle && <MainContent onTitle={onTitle} />}
      </div>
    </div>
  );
};

export default App;
