import React, { useState } from "react";
import "./App.css";
import dummy from "./resource/dummyData";

import ContentList from "./pages/ContentList";
import Create from "./pages/Create";
import View from "./pages/View";

const App = (props) => {
  const [contents, setContents] = useState(dummy);
  const [clickCreate, setClickCreate] = useState(false);
  const [clickTitle, setClickTitle] = useState(false);

  const onCreateBtn = () => {
    setClickCreate((prev) => !prev);
  };

  const onClickTitle = () => {
    console.log("클릭했당 ");
    setClickTitle(true);
  };

  const handleSelect = (username, selectIdx) => {
    const select = contents.filter((content, idx) => idx !== selectIdx);
    setContents(select);
  };

  return (
    <div className="App">
      <h1>React-CRUD-Project</h1>
      <div className="component">
        <div className="list">
          <header>
            <div></div>
            <h2>목록</h2>
            <button className="contentList__btn" onClick={onCreateBtn}>
              글쓰기
            </button>
          </header>
          <ContentList
            contents={contents}
            onClickTitle={onClickTitle}
          ></ContentList>
        </div>

        {clickCreate ? (
          <Create contents={contents} setContents={setContents} />
        ) : (
          <div className="empty"></div>
        )}

        {clickTitle ? (
          <View contents={contents} setContents={setContents} />
        ) : (
          <div></div>
        )}
      </div>
    </div>
  );
};

export default App;
