import React, { useEffect, useState } from "react";
import "./App.css";
import dummy from "./resource/dummyData";

import ContentList from "./pages/ContentList";
import Create from "./pages/Create";
import View from "./pages/View";

const App = (props) => {
  const [contents, setContents] = useState(dummy); // 담긴 데이터 불러오기
  const [clickCreate, setClickCreate] = useState(true); // 글쓰기 버튼이 클릭 되었는가?

  const [selectedContent, setSelectedContent] = useState(dummy[0]);

  // 글쓰기 버튼 클릭 여부에 따라 create 창 띄우기
  const onCreateBtn = () => {
    setClickCreate((prev) => !prev);
  };

  // const onChangeSelectedContent = contents.map((content) => {
  //   setSelectedContent();
  //   console.log("onChangeSelectedContent 싱행 ");
  // });

  const onChangeSelectedContent = (content) => {
    setSelectedContent(content);
  };

  useEffect(() => {});

  return (
    <div className="App">
      <h1>React-CRUD-Project</h1>
      <div className="component">
        <div className="list">
          <header>
            <div></div>
            <h2>목록</h2>
            <button className="contentList__btn" onClick={onCreateBtn}>
              글쓰기
            </button>
          </header>
          {contents.map((content) => {
            return (
              <ContentList
                content={content}
                key={content.id}
                onChangeSelectedContent={onChangeSelectedContent}
              />
            );
          })}
        </div>

        {clickCreate ? (
          <Create contents={contents} setContents={setContents} />
        ) : (
          <div></div>
        )}

        {contents.map((content) => {
          return (
            <View
              content={content}
              key={content.id}
              selectedContent={selectedContent}
            />
          );
        })}
      </div>
    </div>
  );
};

export default App;
